/**
 * Chained converter.
 * It defaults to the basic API provider, and does not require initialization.
 *
 * @example
 *  let value = await Convert(15).from("USD").to("EUR");
 *
 * @export
 * @param {number} amount - amount of currency to convert
 * @returns
 */
export declare function Chainer(amount?: number | undefined): {
    from: (from: string) => any;
    to: (to: string) => Promise<number | undefined>;
    fetch: () => Promise<any>;
    readonly rates: any;
    amount: (val: number) => any;
};
